import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SeatingMap() {
    const [bitmap, setBitmap] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        // Fetch the seating bitmap when the component mounts
        axios.get('http://localhost:3001/seating').then(response => {
            setBitmap(response.data.bitmap);
        });
    }, []);

    const toggleSeat = (seatIndex) => {
        // Toggle the bit at the given seat index
        const newBitmap = bitmap ^ (1 << seatIndex);
        const selectedSeats = newBitmap.toString(2).split('1').length - 1;

        if (selectedSeats > 4) {
            setErrorMessage("You can't select more than 4 seats!");
            return;
        } else {
            setErrorMessage('');
        }

        axios.post('http://localhost:3001/seating', { bitmap: newBitmap }).then(response => {
            setBitmap(response.data.bitmap);
        });
    };

    const computeSeatColor = (seatIndex) => {
        const isSeatSelected = (bitmap & (1 << seatIndex)) !== 0;
        const totalSeatsSelected = bitmap.toString(2).split('1').length - 1;

        if (isSeatSelected) {
            return 'blue';
        } else if (totalSeatsSelected === 4) {
            return 'red';
        } else {
            return 'green';
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 40px)', gap: '10px', marginBottom: '20px' }}>
                {Array.from({ length: 16 }).map((_, seatIndex) => (
                    <button
                        key={seatIndex}
                        onClick={() => toggleSeat(seatIndex)}
                        style={{ width: '40px', height: '40px', backgroundColor: computeSeatColor(seatIndex) }}
                    />
                ))}
            </div>

            <div style={{ display: 'flex', flexDirection: 'row', marginBottom: '20px' }}>
                {Array.from({ length: 16 }).map((_, bitIndex) => (
                    <div key={bitIndex} style={{ width: '20px', height: '20px', marginLeft: '2px', backgroundColor: (bitmap & (1 << bitIndex)) !== 0 ? 'black' : 'white' }} />
                ))}
            </div>

            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
        </div>
    );
}

export default SeatingMap;
