import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUserCircle } from 'react-icons/fa';

function Intersection() {
    const [users, setUsers] = useState([]);
    const [onlineUsers, setOnlineUsers] = useState([]);

    useEffect(() => {
        // Fetch users from server
        axios.get('/getUsers')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error("Error fetching users:", error);
            });
    }, []);

    const toggleUserOnline = (user) => {
        if (onlineUsers.some(onlineUser => onlineUser.id === user.id)) {
            axios.post('/userOffline', { username: user.username })
                .then(() => {
                    setOnlineUsers(prev => prev.filter(onlineUser => onlineUser.id !== user.id));
                })
                .catch(error => {
                    console.error("Error setting user offline:", error);
                });
        } else {
            axios.post('/userOnline', { username: user.username })
                .then(() => {
                    setOnlineUsers(prev => [...prev, user]);
                })
                .catch(error => {
                    console.error("Error setting user online:", error);
                });
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <div>
                <h3>Registered Users</h3>
                <ul>
                    {users.map(user => (
                        <li key={user.id}>
                            {user.username}
                            <button onClick={() => toggleUserOnline(user)}>
                                {onlineUsers.some(onlineUser => onlineUser.id === user.id) ? 'Go Offline' : 'Go Online'}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <h3>Online Users</h3>
                <ul>
                    {onlineUsers.map(onlineUser => (
                        <li key={onlineUser.id}>
                            <FaUserCircle style={{ marginRight: '10px' }} />
                            {onlineUser.username}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Intersection;
