import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CounterSS = () => {
    const [element, setElement] = useState('');
    const [score, setScore] = useState('');
    const [rankingData, setRankingData] = useState([]);
    const [selectedElement, setSelectedElement] = useState('');

    useEffect(() => {
        fetchRankingData();
    }, []);

    const fetchRankingData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/getRanking');
            setRankingData(response.data);
        } catch (error) {
            console.error("Error fetching ranking data:", error);
        }
    };

    const handleAddElement = async () => {
        try {
            await axios.post('http://localhost:3001/addElement', {
                element,
                score: parseFloat(score)
            });
            fetchRankingData();  // refresh the ranking data
        } catch (error) {
            console.error("Error adding element:", error);
        }
    };

    const handleUpdateScore = async () => {
        try {
            await axios.post('http://localhost:3001/updateScore', {
                element: selectedElement,
                score: parseFloat(score)
            });
            fetchRankingData();  // refresh the ranking data
        } catch (error) {
            console.error("Error updating score:", error);
        }
    };

    return (
        <div>
            <input
                placeholder="Element Name"
                value={element}
                onChange={e => setElement(e.target.value)}
            />
            <input
                placeholder="Score"
                type="number"
                value={score}
                onChange={e => setScore(e.target.value)}
            />
            <button onClick={handleAddElement}>Add Element</button>

            <select value={selectedElement} onChange={e => setSelectedElement(e.target.value)}>
                {rankingData.map((item, index) => (
                    <option key={index} value={item.element}>
                        {item.element}
                    </option>
                ))}
            </select>
            <button onClick={handleUpdateScore}>Update Score</button>

            <table>
                <thead>
                    <tr>
                        <th>Rank</th>
                        <th>Element</th>
                        <th>Score</th>
                    </tr>
                </thead>
                <tbody>
                    {rankingData.map((item, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{item.element}</td>
                            <td>{item.score}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default CounterSS;
