        if (!isSelected && totalSelected === 4) {
            alert("You can't select more than 4 seats!");
            return;
        }

        axios.post('http://localhost:3001/setSeat', { seatIndex }).then(response => {
            setBitmap(response.data.bitmap);
        }).catch(err => {
