import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
    const [cartItems, setCartItems] = useState({});
    const [totalPrice, setTotalPrice] = useState(0);

    const fetchCartItems = () => {
        axios.get('http://localhost:3001/fetchCartItems')
            .then(response => {
                setCartItems(response.data.cart || {});
                const total = Object.values(response.data.cart || {}).reduce((acc, curr) => acc + parseFloat(curr), 0);
                setTotalPrice(total);
            })
            .catch(error => {
                console.error("Error fetching cart:", error);
            });
    };

    useEffect(fetchCartItems, []); // fetch cart contents on mount

    const removeFromCart = (seat) => {
        axios.post('http://localhost:3001/removeFromCart', { seat })
            .then(fetchCartItems) // refresh the cart after removing a seat
            .catch(error => {
                console.error("Error removing from cart:", error);
            });
    };

    const addToCart = () => {
        axios.post('http://localhost:3001/addToCart')
            .then(response => {
                console.log(response.data);
                fetchCartItems(); // refresh the cart after adding seats
            })
            .catch(error => {
                console.error("Error adding to cart:", error);
            });
    };

    return (
        <div className="cart-container">
            <h2>Shopping Cart</h2>
            <ul>
                {Object.entries(cartItems).map(([seat, price]) => (
                    <li key={seat}>
                        Seat: {seat} - Price: ${price}
                        <button onClick={() => removeFromCart(seat)}>Remove</button>
                    </li>
                ))}
            </ul>
            <div>
                Total Price: ${totalPrice.toFixed(2)}
            </div>
            <button onClick={addToCart}>Add to Cart</button>
        </div>
    );
}

export default Cart;
