import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = 20; // Adjust this to your desired seat price.

function Cart() {
    const [cart, setCart] = useState({});

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = () => {
        axios.get('http://localhost:3001/cart')
            .then(response => {
                setCart(response.data.cart);
            })
            .catch(() => alert("Error fetching the cart."));
    };

    const clearCart = () => {
        axios.post('http://localhost:3001/clearCart')
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                } else {
                    alert(response.data.error);
                }
            })
            .catch(() => alert("Error clearing the cart."));
    };

    const removeSeatFromCart = (seat) => {
        axios.post('http://localhost:3001/removeFromCart', { seat })
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                } else {
                    alert(response.data.error);
                }
            })
            .catch(() => alert("Error removing the seat from the cart."));
    };

    const totalSeats = Object.values(cart).reduce((acc, curr) => acc + curr, 0);
    const totalPrice = totalSeats * SEAT_PRICE;

    return (
        <div>
            {/* Cart representation */}
            <div>
                {Object.entries(cart).map(([seat, count]) => (
                    <div key={seat}>
                        <span>Seat {seat}: {count} x ${SEAT_PRICE} = ${count * SEAT_PRICE}</span>
                        <button onClick={() => removeSeatFromCart(seat)}>Remove</button>
                    </div>
                ))}
            </div>

            {/* Cart Total */}
            <div>
                <h2>Total: ${totalPrice}</h2>
            </div>

            {/* Clear Cart Button */}
            <div>
                <button onClick={clearCart}>Clear Cart</button>
            </div>
        </div>
    );
}

export default Cart;
