import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SeatingMap() {
    const [bitmap, setBitmap] = useState(0);

    useEffect(() => {
        axios.get('http://localhost:3001/seating')
            .then(response => {
                setBitmap(response.data.bitmap);
            });
    }, []);

    const toggleSeat = (seatIndex) => {
        const updatedBitmap = bitmap ^ (1 << seatIndex);
        const selectedSeats = updatedBitmap.toString(2).split('1').length - 1;

        if (selectedSeats <= 4) {
            axios.post('http://localhost:3001/seating', { bitmap: updatedBitmap })
                .then(response => {
                    setBitmap(response.data.bitmap);
                });
        } else {
            alert("You can't select more than 4 seats!");
        }
    };

    const computeSeatColor = (seatIndex) => {
        const isSeatSelected = (bitmap & (1 << seatIndex)) !== 0;
        const bookingLimitReached = bitmap.toString(2).split('1').length - 1 >= 4;

        if (isSeatSelected) {
            return 'blue';
        } else if (bookingLimitReached) {
            return 'red';
        } else {
            return 'green';
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div className="seating-container" style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 40px)', gap: '10px', marginBottom: '20px' }}>
                {Array.from({ length: 16 }).map((_, seatIndex) => (
                    <button 
                        key={seatIndex} 
                        onClick={() => toggleSeat(seatIndex)} 
                        style={{ width: '40px', height: '40px', backgroundColor: computeSeatColor(seatIndex) }}
                    >
                    </button>
                ))}
            </div>

            <div className="bitmap-container" style={{ display: 'grid', gridTemplateColumns: 'repeat(16, 20px)', gap: '5px' }}>
                {Array.from({ length: 16 }).map((_, bitIndex) => (
                    <div 
                        key={bitIndex} 
                        style={{ 
                            width: '20px', 
                            height: '20px', 
                            backgroundColor: (bitmap & (1 << bitIndex)) !== 0 ? 'black' : 'white' 
                        }}
                    >
                    </div>
                ))}
            </div>
        </div>
    );
}

export default SeatingMap;
