// Cart.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        // Fetch cart items from server when the component mounts
        const fetchCart = async () => {
            try {
                const response = await axios.get('/getCart');
                if (response.data && response.data.cart) {
                    setCartItems(response.data.cart);
                    // Calculate total price based on the number of seats and each seat price
                    setTotalPrice(response.data.cart.length * 5); // assuming each seat is $5
                }
            } catch (error) {
                console.error("Failed to fetch cart:", error.message);
            }
        };

        fetchCart();
    }, []);

    const handleRemove = async (seatIndex) => {
        try {
            await axios.post('/removeFromCart', { seatIndex });
            // Update the local state
            const updatedCartItems = cartItems.filter(seat => seat !== seatIndex);
            setCartItems(updatedCartItems);
            setTotalPrice(updatedCartItems.length * 5); // update total price
        } catch (error) {
            console.error("Failed to remove item:", error.message);
        }
    };

    return (
        <div>
            <h2>Your Cart</h2>
            <ul>
                {cartItems.map((seatIndex, idx) => (
                    <li key={idx}>
                        Seat: {seatIndex}
                        <button onClick={() => handleRemove(seatIndex)}>Remove</button>
                    </li>
                ))}
            </ul>
            <div>Total Price: ${totalPrice}</div>
        </div>
    );
}

export default Cart;
