import React, { useState } from 'react';
import SeatingMap from './SeatingMap';
import axios from 'axios';

const SEAT_PRICE = 5;  // Price for one seat

function Cart() {
    const [cart, setCart] = useState([]);  // To maintain the selected seats
    const [total, setTotal] = useState(0);  // To maintain the total price

    // This function is passed to SeatingMap to handle when seats are selected
    const handleSeatsSelected = async (selectedSeats) => {
        setCart(selectedSeats);

        // Optionally, you can persist the cart to the server (if needed)
        // For instance, you could send selectedSeats to the server via axios

        // Calculate the total price and set it
        setTotal(selectedSeats.length * SEAT_PRICE);
    };

    return (
        <div>
            <h2>Your Cart</h2>

            <SeatingMap onSeatsSelected={handleSeatsSelected} />

            <h3>Selected Seats:</h3>
            <ul>
                {cart.map((seat, index) => (
                    <li key={index}>Seat {seat + 1}</li>  // +1 to make it 1-based instead of 0-based
                ))}
            </ul>

            <h3>Total: ${total}</h3>
        </div>
    );
}

export default Cart;
