import React, { useState, useEffect, useRef } from 'react';

function Monitor() {
    const [commands, setCommands] = useState([]);
    const wsRef = useRef();

// New state for showing or hiding the code
const [showCode, setShowCode] = useState(false);

// Function to toggle the visibility of the code block
const toggleCode = () => {
    setShowCode(prevState => !prevState);
}


    useEffect(() => {
        setTimeout(() => {
        wsRef.current = new WebSocket('ws://localhost:3002');

        wsRef.current.onopen = () => {
            console.log('WebSocket connected.');
        };

        wsRef.current.onmessage = (message) => {
            const command = JSON.parse(message.data);
            console.log(`Received command from server: ${command}`);
            setCommands(prevCommands => {
                const newCommands = [...prevCommands, command.join(' ')];
                return newCommands.slice(-9);  // keep only the last 10 commands
            });
        };        

        wsRef.current.onerror = (error) => {
            console.error(`WebSocket Error: ${error}`);
        };

        wsRef.current.onclose = () => {
            console.log('WebSocket closed.');
        };

        return () => {
            if (wsRef.current) {
                wsRef.current.close();
            }
        }}, 1000); // waits 1 second before attempting to connect
    }, []);

    return (
        <div>
            <div className="terminal" style={{ width: '60%'}}>
                {commands.map((command, index) => (
                    <div className="command" key={index}>{command}</div>
                ))}
            </div>
            <div style={{ width: '60%', display: 'flex', justifyContent: 'flex-start', marginTop: '10px' }}>
            <button className="code-button" onClick={toggleCode} style={{ flex: 1 }}>
          {showCode ? "Hide the Code" : "Show the Code"}
        </button>
            </div>
            {showCode && (
                <div className="code-container">
                    <pre>
                        <code>
                            {/* [Your desired code here] */}
                        </code>
                    </pre>
                </div>
            )}
        </div>
    );


}

export default Monitor;
