import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
    const [selectedSeatIndices, setSelectedSeatIndices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetching the selected seats when the component mounts
        axios.get('http://localhost:3001/selectedSeats')
        .then((response) => {
            setSelectedSeatIndices(response.data);
            setLoading(false);
        })
        .catch((error) => {
            console.error("Error fetching selected seats:", error);
            setLoading(false);
        });
    }, []);

    const fetchSelectedSeats = () => {
        axios.get('http://localhost:3001/selectedSeats')
        .then(res => {
            const seatsString = res.data.seats;
            const seatsArr = Array.from(seatsString);
            const selected = seatsArr.map((seat, index) => seat === '1' ? index : null).filter(Boolean);
            setSelectedSeats(selected);
        })
        .catch(err => alert('Error fetching selected seats.'));
