import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = 5;

function Cart() {
    const [cart, setCart] = useState({});

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = () => {
        axios.get('http://localhost:3001/cart').then(response => {
            setCart(response.data);
        }).catch(err => {
            alert('Error fetching the cart.');
        });
    };

    const addToCart = () => {
        axios.post('http://localhost:3001/addToCart').then(response => {
            fetchCart();  // Update the cart once seats have been added
        }).catch(err => {
            alert('Error adding to the cart.');
        });
    };

    const totalSeats = Object.values(cart).reduce((acc, curr) => acc + curr, 0);
    const totalPrice = totalSeats * SEAT_PRICE;

    return (
        <div>
            <button onClick={addToCart}>Add to Cart</button>
            <div>
                <h2>Your Cart</h2>
                {Object.entries(cart).map(([seat, count]) => (
                    <div key={seat}>
                        <span>Seat {seat}: {count} x ${SEAT_PRICE} = ${count * SEAT_PRICE}</span>
                    </div>
                ))}
                <hr />
                <div>Total: ${totalPrice}</div>
            </div>
        </div>
    );
}

export default Cart;
