import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Users() {
    const [users, setUsers] = useState([]);
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [email, setEmail] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    // New state for showing or hiding the code
const [showCode, setShowCode] = useState(false);

// Function to toggle the visibility of the code block
const toggleCode = () => {
    setShowCode(prevState => !prevState);
}

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/users');
            setUsers(response.data);
        } catch (error) {
            console.error("Error fetching users", error);
        }
    };

    const addUserHandler = async () => {
        try {
            await axios.post('http://localhost:3001/users', { username, firstName, lastName, dateOfBirth, email });
            fetchUsers();
            setUsername('');
            setFirstName('');
            setLastName('');
            setDateOfBirth('');
            setEmail('');
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    const deleteUserHandler = async (userId) => {
        try {
            await axios.delete(`http://localhost:3001/users/${userId}`);
            fetchUsers();
        } catch (error) {
            console.error("Error deleting user", error);
        }
    };

    // CSS Styles
    const inputStyle = {
        padding: '10px',
        borderRadius: '5px',
        margin: '0 10px 20px 0',
        fontSize: '18px',
        width: 'calc(30% - 10px)'
    };

    const buttonStyleCode = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3a3a3a',
        color: 'white',
        border: 'none',
        cursor: 'pointer',
        marginRight: '10px'
    };

    const buttonStyle = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3498db',
        color: 'white',
        border: 'none',
        cursor: 'pointer',
        marginRight: '10px'
    };

    const titleStyle = {
        borderBottom: '2px solid #3498db',
        paddingBottom: '10px',
        marginTop: '20px'
    };
        // Updated CSS Styles
        const tableStyle = {
            width: '100%',
            marginTop: '20px',
            borderCollapse: 'collapse',
        };

        const tableHeaderStyle = {
            backgroundColor: '#3498db',
            color: 'white',
            fontWeight: 'bold',
            padding: '10px 15px',
            borderBottom: '2px solid #2980b9',
        };

        const tableRowStyle = {
            borderBottom: '1px solid #ccc',
        };

        const tableDataStyle = {
            padding: '10px 15px',
            textAlign: 'left',
        };

    return (
        <div>
            <h3 style={titleStyle}>Add User</h3>
            <div>
                <input style={inputStyle} value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
                <input style={inputStyle} value={firstName} onChange={(e) => setFirstName(e.target.value)} placeholder="First Name" />
                <input style={inputStyle} value={lastName} onChange={(e) => setLastName(e.target.value)} placeholder="Last Name" />
                <input style={inputStyle} type="date" value={dateOfBirth} onChange={(e) => setDateOfBirth(e.target.value)} placeholder="Date of Birth" />
                <input style={inputStyle} value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
                <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
            </div>

            <h3 style={titleStyle}>Existing Users</h3>
            <table className="ranking-table" style={{ width: '100%'}} >
                <thead>
                    <tr>
                        <th style={{ fontSize: '20px', textAlign: 'left'}}>Username</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>First Name</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Last Name</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Date of Birth</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Email</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.userId} style={tableRowStyle}>
                            <td style={tableDataStyle}>{user.username}</td>
                            <td style={tableDataStyle}>{user.firstName}</td>
                            <td style={tableDataStyle}>{user.lastName}</td>
                            <td style={tableDataStyle}>{user.dateOfBirth}</td>
                            <td style={tableDataStyle}>{user.email}</td>
                            <td style={tableDataStyle}>
                                <button style={buttonStyle} onClick={() => deleteUserHandler(user.userId)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

           {/* Show/Hide Code button */}
  <div style={{ width: '100%', display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
          <button className="code-button" onClick={toggleCode} style={buttonStyleCode}>
              {showCode ? "Hide the Code" : "Show the Code"}
          </button>
      </div>

      {showCode && (
    <div className="code-container" style={{ width: '100%', marginBottom: '20px', marginTop: '20px' }}>
        <pre style={{ padding: '20px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', whiteSpace: 'pre-wrap', overflowWrap: 'anywhere', background: '#f7f7f7' }}>
            <code>
{`//////////////////////////////////////////////// Sorted Set counter
///Add Element to the Sorted Set
app.post('/addElement', (req, res) => {
    const { element, score } = req.body;
    client.zadd('sortedSet', score, element, (err, reply) => {
        if (err) {
            res.status(500).send({ error: err.message });
            return;
        }
        res.status(200).send({ added: true });
    });
});

///Get All Elements from the Sorted Set
app.get('/getAllElements', (req, res) => {
    client.zrange('sortedSet', 0, -1, 'WITHSCORES', (err, elements) => {
        if (err) {
            res.status(500).send({ error: err.message });
            return;
        }
        let result = [];
        for (let i = 0; i < elements.length; i += 2) {
            result.push({ element: elements[i], score: elements[i + 1] });
        }
        res.status(200).send(result);
    });
});

///Update Score of an Element
app.put('/updateScore', (req, res) => {
    const { element, newScore } = req.body;
    client.zadd('sortedSet', newScore, element, (err, reply) => {
        if (err) {
            res.status(500).send({ error: err.message });
            return;
        }
        res.status(200).send({ updated: true });
    });
});

///Fetch Ranked Elements
app.get('/getRankedElements', (req, res) => {
    client.zrevrange('sortedSet', 0, -1, 'WITHSCORES', (err, elements) => {
        if (err) {
            res.status(500).send({ error: err.message });
            return;
        }
        let result = [];
        for (let i = 0; i < elements.length; i += 2) {
            result.push({ element: elements[i], score: elements[i + 1] });
        }
        res.status(200).send(result);
    });
});

app.delete('/deleteElement', (req, res) => {
    const { element } = req.body;

    client.zrem('sortedSet', element, (err, response) => {
        if (err) {
            return res.status(500).json({ message: "Error deleting element", error: err.message });
        }
        res.json({ message: "Element deleted successfully" });
    });
});


app.get('/getRanking', async (req, res) => {
    try {
        const ranking = await getRankingFromRedis();
        res.status(200).json(ranking);
    } catch (error) {
        console.error("Error:", error);  // This will print the error details in the backend console
        res.status(500).json({ message: "Error fetching ranking", error: error.message });
    }

});

const getRankingFromRedis = async () => {
    return new Promise((resolve, reject) => {
        client.zrevrange('sortedSet', 0, -1, 'WITHSCORES', (err, result) => {
            if (err) return reject(err);

            let ranking = [];
            for (let i = 0; i < result.length; i += 2) {
                ranking.push({
                    element: result[i],
                    score: result[i + 1]
                });
            }
            resolve(ranking);
        });
    });
};


//////////////////////////////////////////////`}
            </code>
        </pre>
    </div>
)}   
        </div>
    );
}

export default Users;
