import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useUsers } from './UserContext';

const titleStyle = {
    borderBottom: '2px solid #3498db',
    paddingBottom: '10px',
    marginTop: '20px'
};

const inputStyle = {
    margin: '10px',
    padding: '10px',
    fontSize: '16px'
};

const buttonStyle = {
    padding: '10px 15px',
    borderRadius: '5px',
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    cursor: 'pointer'
};

function Users() {
    const { users, setUsers, onlineUsers, setOnlineUsers } = useUsers();
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [premium, setPremium] = useState('No');
    const [geo, setGeo] = useState('Africa');

    useEffect(() => {
        async function fetchUsers() {
            try {
                const response = await axios.get('http://localhost:3001/users');
                setUsers(response.data);
            } catch (error) {
                console.error("Error fetching users", error);
            }
        }

        fetchUsers();
    }, [setUsers]);

    const addUserHandler = async () => {
        try {
            const newUser = { username, firstName, lastName, premium, geo };
            const response = await axios.post('http://localhost:3001/users', newUser);
            if (response.data) {
                setUsers(prevUsers => [...prevUsers, response.data]);
            }

            // Reset fields
            setUsername('');
            setFirstName('');
            setLastName('');
            setPremium('No');
            setGeo('Africa');
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    const deleteUserHandler = async (userId) => {
        try {
            const response = await axios.delete(`http://localhost:3001/users/${userId}`);
            if (response.status === 200) {
                setUsers(prevUsers => prevUsers.filter(user => user.userId !== userId));

                if (onlineUsers.includes(userId)) {
                    setOnlineUsers(prevOnlineUsers => prevOnlineUsers.filter(id => id !== userId));
                }
            }
        } catch (error) {
            console.error("Error deleting user", error);
        }
    };

    return (
        <div>
            <div>
    <h3 style={titleStyle}>Add User</h3>
    <div>
        {/* ... (existing input fields and select dropdowns) ... */}
        <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
    </div>
</div>

<div style={{ marginTop: '40px' }}>
    <h3 style={titleStyle}>User List</h3>
    <table style={{ margin: '20px', width: '90%', borderCollapse: 'collapse' }}>
        <thead>
            <tr>
                <th style={tableHeaderStyle}>Username</th>
                <th style={tableHeaderStyle}>First Name</th>
                <th style={tableHeaderStyle}>Last Name</th>
                <th style={tableHeaderStyle}>Premium</th>
                <th style={tableHeaderStyle}>Geo</th>
                <th style={tableHeaderStyle}>Actions</th>
            </tr>
        </thead>
        <tbody>
            {users.filter(user => user && user.userId).map(user => (
                <tr key={user.userId}>
                    <td style={tableCellStyle}>{user.username}</td>
                    <td style={tableCellStyle}>{user.firstName}</td>
                    <td style={tableCellStyle}>{user.lastName}</td>
                    <td style={tableCellStyle}>{user.premium}</td>
                    <td style={tableCellStyle}>{user.geo}</td>
                    <td style={tableCellStyle}>
                        <button style={{ ...buttonStyle, backgroundColor: '#f44336' }} onClick={() => deleteUserHandler(user.userId)}>Delete</button>
                    </td>
                </tr>
            ))}
        </tbody>
    </table>
</div>


