import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    fetchCartData();
  }, []);

  const fetchCartData = async () => {
    try {
      const response = await axios.get('http://localhost:3001/cart');  // Assuming there's an endpoint to fetch cart data
      if (response.data && response.data.cart) {
        setCart(Object.entries(response.data.cart));
        setTotal(response.data.total);
      }
    } catch (error) {
      console.error("Error fetching cart data:", error);
    }
  };

  const addToCart = async () => {
    try {
      await axios.post('http://localhost:3001/addToCart');
      fetchCartData();  // Refetch cart data after adding
    } catch (error) {
      console.error("Error adding to cart:", error);
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 && <p>Your cart is empty</p>}
      <ul>
        {cart.map(([seat, price], index) => (
          <li key={index}>
            Seat {seat}: ${price}
          </li>
        ))}
      </ul>
      <p>Total: ${total}</p>
      <button onClick={addToCart}>Add to Cart</button>
    </div>
  );
}

export default Cart;
