function Cart({ selectedSeats, pricePerSeat }) {
    const totalPrice = selectedSeats.length * pricePerSeat;

    return (
      <div>
        <h2>Your Cart</h2>
        <ul>
          {selectedSeats.map((seat, index) => (
            <li key={index}>Seat {seat + 1}</li>
          ))}
        </ul>
        <h3>Total: ${totalPrice.toFixed(2)}</h3>
      </div>
    );
  }

  export default Cart;
