import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
    const [cartSeats, setCartSeats] = useState([]);
    const [selectedSeats, setSelectedSeats] = useState([]);

    const fetchCart = () => {
        axios.get('http://localhost:3001/getCart')
        .then(res => {
            setCartSeats(res.data.cart);
        })
        .catch(err => alert('Error fetching cart.'));
    };

    const fetchSelectedSeats = () => {
        axios.get('http://localhost:3001/seats')
        .then(res => {
            const seatsString = res.data.seats;
            const seatsArr = Array.from(seatsString);
            const selected = seatsArr.map((seat, index) => seat === '1' ? index : null).filter(Boolean);
            setSelectedSeats(selected);
        })
        .catch(err => alert('Error fetching selected seats.'));
    };

    const addToCart = () => {
        fetchSelectedSeats();
        axios.post('http://localhost:3001/addToCart', { selectedSeats })
        .then(res => {
            fetchCart();
        })
        .catch(err => alert('Error adding to cart.'));
    };

    const removeFromCart = (seat) => {
        axios.post('http://localhost:3001/removeFromCart', { seat })
        .then(res => {
            fetchCart();
        })
        .catch(err => alert('Error removing from cart.'));
    };

    const clearCart = () => {
        axios.post('http://localhost:3001/clearCart')
        .then(res => {
            fetchCart();
        })
        .catch(err => alert('Error clearing the cart.'));
    };

    useEffect(() => {
        fetchCart();
    }, []);

    return (
        <div>
            <h3>Cart Seats:</h3>
            {Array.isArray(cartSeats) && cartSeats.map((seat, index) => (
    <div key={index}>
        {seat} 
        <button onClick={() => removeFromCart(seat)}>Remove</button>
    </div>
))}
            <button onClick={addToCart}>Add to Cart</button>
            <button onClick={clearCart}>Clear Cart</button>
        </div>
    );
}

export default Cart;
