import React, { useState } from 'react';

function Users() {
    const [users, setUsers] = useState([]);

    const [newUser, setNewUser] = useState({
        username: '',
        firstname: '',
        lastname: '',
        dob: '',
        email: ''
    });

    const addUserHandler = () => {
        setUsers([...users, {...newUser, id: Date.now()}]);
        setNewUser({
            username: '',
            firstname: '',
            lastname: '',
            dob: '',
            email: ''
        });
    };

    const inputStyle = {
        margin: '10px',
        padding: '5px',
        borderRadius: '3px',
        border: '1px solid #ccc'
    };

    const buttonStyle = {
        padding: '5px 10px',
        borderRadius: '3px',
        cursor: 'pointer'
    };

    const titleStyle = {
        textAlign: 'center',
        margin: '20px 0'
    };

    const userListStyle = {
        border: '1px solid #ddd',
        borderRadius: '5px',
        padding: '10px',
        boxShadow: '2px 2px 5px rgba(0,0,0,0.1)'
    };

    const userItemStyle = {
        padding: '10px',
        borderBottom: '1px solid #eee'
    };

    return (
        <div>
            <h3 style={titleStyle}>Add New User</h3>
            <div>
                <input
                    style={inputStyle}
                    value={newUser.username}
                    onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                    placeholder="Username"
                />
                <input
                    style={inputStyle}
                    value={newUser.firstname}
                    onChange={(e) => setNewUser({ ...newUser, firstname: e.target.value })}
                    placeholder="First Name"
                />
                <input
                    style={inputStyle}
                    value={newUser.lastname}
                    onChange={(e) => setNewUser({ ...newUser, lastname: e.target.value })}
                    placeholder="Last Name"
                />
                <input
                    style={inputStyle}
                    value={newUser.dob}
                    onChange={(e) => setNewUser({ ...newUser, dob: e.target.value })}
                    placeholder="Date of Birth (YYYY-MM-DD)"
                />
                <input
                    style={inputStyle}
                    value={newUser.email}
                    onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    placeholder="Email"
                />
                <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
            </div>
            <h3 style={titleStyle}>User List</h3>
            <div style={userListStyle}>
                {users.map(user => (
                    <div style={userItemStyle} key={user.id}>
                        {user.username} - {user.firstname} {user.lastname} - {user.dob} - {user.email}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Users;
