import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart({ userId }) {
    const [cart, setCart] = useState([]);

    useEffect(() => {
        // Fetch the cart from server on component mount
        axios.get(`/getCart?userId=${userId}`)
            .then(response => {
                setCart(response.data.cart);
            });
    }, [userId]);

    const removeSeat = (seat) => {
        axios.post('/removeSeat', { userId, seat })
            .then(response => {
                if(response.data.success) {
                    setCart(prevCart => prevCart.filter(s => s !== seat));
                }
            });
    };

    const clearCart = () => {
        axios.post('/clearCart', { userId })
            .then(response => {
                if(response.data.success) {
                    setCart([]);
                }
            });
    };

    return (
        <div>
            <h3>Your Cart</h3>
            <ul>
                {cart.map(seat => (
                    <li key={seat}>
                        Seat {seat + 1}
                        <button onClick={() => removeSeat(seat)}>Remove</button>
                    </li>
                ))}
            </ul>
            <button onClick={clearCart}>Clear Cart</button>
        </div>
    );
}

export default Cart;
