// RedisMonitor.js

import React, { useState, useEffect } from 'react';

function RedisMonitor() {
    const [output, setOutput] = useState([]);
    const [showCode, setShowCode] = useState(false);

    useEffect(() => {
        const ws = new WebSocket('ws://localhost:3001/monitor');

        ws.onmessage = (message) => {
            const newOutput = JSON.parse(message.data);
            setOutput(newOutput);
        };

        return () => ws.close();
    }, []);

    const toggleCode = () => {
        setShowCode(prevState => !prevState);
    }

    return (
        <div className="column">
            <div className="card">
                <p>Redis Monitor Output:</p>
                {output.map((line, idx) => (
                    <p key={idx}>{line}</p>
                ))}
            </div>

            <button className="code-button" onClick={toggleCode}>
                {showCode ? "Hide the Code" : "Show the Code"}
            </button>          

            {showCode && (
                <div className="code-container">
                    <pre>
                        <code>
                            // [Your desired code here]
                        </code>
                    </pre>
                </div>
            )}
        </div>
    );
}

export default RedisMonitor;
