import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Counter() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    // Fetch the initial counter value when the component mounts
    axios.get('http://localhost:3001/counter').then(response => {
      setCount(response.data.value);
    });
  }, []);

  const increment = () => {
    axios.post('http://localhost:3001/increment').then(response => {
      setCount(response.data.value);
    });
  };

  const decrement = () => {
    axios.post('http://localhost:3001/decrement').then(response => {
      setCount(response.data.value);
    });
  };

  const [showCode, setShowCode] = useState(false);
  const toggleCode = () => {
    setShowCode(prevState => !prevState);
}


  return (
    <div className="counter-container"> {/* Apply the styling class */}
        <div>
            Count: {count}
        </div>
        <div>&nbsp;&nbsp;&nbsp;</div>
        <div>
            <button onClick={increment}>Increment</button>
            <div>&nbsp;&nbsp;&nbsp;</div>
            <button onClick={decrement}>Decrement</button>
        </div>
        <div>
                <button onClick={toggleCode}>
                    {showCode ? "Hide the Code" : "Show the Code"}
                </button>
                {showCode && (
                    <pre>
                        <code>
                            {`
                                // This is the unified code block
                                // You can put all the code you want to show here
                                // ...
                            `}
                        </code>
                    </pre>
                )}
            </div>
    </div>
);
}


export default Counter;
