import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUserCircle } from 'react-icons/fa';

function Intersection() {
    const [users, setUsers] = useState([]);
    const [onlineUsers, setOnlineUsers] = useState([]);

    useEffect(() => {
        // Fetch users from server
        axios.get('http://localhost:3001/getUsers')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error("Error fetching users:", error);
            });

        // Fetch online users
        axios.get('http://localhost:3001/getOnlineUsers')
            .then(response => {
                setOnlineUsers(response.data);
            })
            .catch(error => {
                console.error("Error fetching online users:", error);
            });
    }, []);

    const toggleUserOnline = (user) => {
        if (onlineUsers.includes(user.username)) {
            axios.post('http://localhost:3001/userOffline', { username: user.username })
                .then(() => {
                    setOnlineUsers(prev => prev.filter(onlineUser => onlineUser !== user.username));
                })
                .catch(error => {
                    console.error("Error setting user offline:", error);
                });
        } else {
            axios.post('http://localhost:3001/userOnline', { username: user.username })
                .then(() => {
                    setOnlineUsers(prev => [...prev, user.username]);
                })
                .catch(error => {
                    console.error("Error setting user online:", error);
                });
        }
    };

    const sectionStyle = {
        flex: '0 48%',
        border: '1px solid #e0e0e0',
        borderRadius: '8px',
        padding: '20px',
        boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.05)',
        height: 'max-content'
    };

    const titleStyle = {
        borderBottom: '2px solid black',
        paddingBottom: '10px',
        marginBottom: '20px',
        color: 'black',
        fontSize: '24px'
    };

    const listStyle = {
        listStyleType: 'none',
        padding: 0,
        maxHeight: '200px',  // Arbitrary height. You can adjust this.
        overflowY: 'auto'
    };

    const listItemStyle = {
        marginBottom: '10px',
        height: '30px',
        display: 'flex',
        alignItems: 'center'
    };

    const buttonStyle = {
        marginLeft: '15px',
        padding: '5px 10px',
        cursor: 'pointer',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
        transition: 'background-color 0.3s'
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', gap: '2%' }}>
            <div style={sectionStyle}>
                <h3 style={titleStyle}>Registered Users</h3>
                <ul style={listStyle}>
                    {users.filter(user => user).map(user => (
                        <li key={user.id} style={listItemStyle}>
                            {user.username}
                            <button style={buttonStyle} onClick={() => toggleUserOnline(user)}>
                                {onlineUsers.includes(user.username) ? 'Go Offline' : 'Go Online'}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
            <div style={sectionStyle}>
                <h3 style={titleStyle}>Online Users</h3>
                <ul style={listStyle}>
                    {users.map(user => (
                        <li key={user.id} style={listItemStyle}>
                            {onlineUsers.includes(user.username) && <FaUserCircle style={{ marginRight: '10px' }} />}

                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Intersection;
