import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Users() {
    const [users, setUsers] = useState([]);
    const [newUserName, setNewUserName] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const [showCode, setShowCode] = useState(false);

    const toggleCode = () => {
        setShowCode(prevState => !prevState);
    };

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/users');
            setUsers(response.data);
        } catch (error) {
            console.error("Error fetching users", error);
        }
    };

    const addUserHandler = async () => {
        try {
            await axios.post('http://localhost:3001/users', { name: newUserName });
            fetchUsers();
            setNewUserName('');
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    const inputStyle = {
        padding: '10px',
        borderRadius: '5px',
        margin: '0 10px 20px 0',
        fontSize: '18px',
        width: 'calc(50% - 10px)'
    };

    const buttonStyle = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3498db',
        color: 'white',
        border: 'none',
        cursor: 'pointer'
    };

    const titleStyle = {
        borderBottom: '2px solid #3498db',
        paddingBottom: '10px',
        marginTop: '20px'
    };

    return (
        <div>
            <h3 style={titleStyle}>Users List</h3>
            {users.map(user => (
                <div key={user.id}>{user.name}</div>
            ))}
            <h3 style={titleStyle}>Add User</h3>
            <div>
                <input style={inputStyle} value={newUserName} onChange={(e) => setNewUserName(e.target.value)} placeholder="Enter user name" />
                <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
            </div>

            {/* Show/Hide Code button */}
            <div style={{ width: '100%', display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                <button className="code-button" onClick={toggleCode} style={{ padding: '10px 20px', fontSize: '18px' }}>
                    {showCode ? "Hide the Code" : "Show the Code"}
                </button>
            </div>

            {showCode && (
                <div className="code-container" style={{ width: '100%', marginBottom: '20px', marginTop: '20px' }}>
                    <pre style={{ padding: '20px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', whiteSpace: 'pre-wrap', overflowWrap: 'anywhere', background: '#f7f7f7' }}>
                        <code>
                            {/* Place the server-side code related to users here, similar to the Sorted Set example provided. */}
                            {`Your server-side code related to users goes here...`}
                        </code>
                    </pre>
                </div>
            )}
        </div>
    );
}

export default Users;
