import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = 5; // $5 per seat

function Cart({ selectedSeats }) { // receive the selectedSeats as a prop
    const [cart, setCart] = useState({});

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = () => {
        axios.get('http://localhost:3001/cart')
            .then(response => {
                setCart(response.data.cart);
            })
            .catch(() => alert("Error fetching the cart."));
    };

    const addToCart = () => {
        axios.post('http://localhost:3001/addToCart', { selectedSeats })
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                } else {
                    alert(response.data.error);
                }
            })
            .catch(error => {
                alert("Error adding to the cart.");
            });
    };

    const clearCart = () => {
        axios.post('http://localhost:3001/clearCart')
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                }
            })
            .catch(() => alert("Error clearing the cart."));
    };

    const removeSeat = (seatIndex) => {
        axios.post(`http://localhost:3001/removeFromCart`, { seat: seatIndex })
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                }
            })
            .catch(() => alert("Error removing the seat from the cart."));
    };

    return (
        <div>
            {/* Add to Cart Button */}
            <div>
                {selectedSeats.length > 0 && <button onClick={addToCart}>Add to Cart</button>}
            </div>

            {/* Cart Representation */}
            <div>
                {Object.entries(cart).map(([seatPosition, count]) => (
                    <div key={seatPosition}>
                        <span>Seat {seatPosition}: </span>
                        <span>{count} x ${SEAT_PRICE} = ${count * SEAT_PRICE}</span>
                        <button onClick={() => removeSeat(seatPosition)}>Remove</button>
                    </div>
                ))}
            </div>

            {/* Cart Total */}
            <div>
                Total: ${Object.values(cart).reduce((acc, count) => acc + count * SEAT_PRICE, 0)}
            </div>

            {/* Clear Cart Button */}
            <div>
                <button onClick={clearCart}>Clear Cart</button>
            </div>
        </div>
    );
}

export default Cart;
