import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cart.css';


const titleStyle = {
    borderBottom: '2px solid #3498db',
    paddingBottom: '10px',
    marginTop: '20px'
};

function Cart({ selectedSeats, onSeatsSelected }) {
    const [cart, setCart] = useState({});
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        fetchCartData();
    }, []);

    const fetchCartData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/fetchCartItems');
            if (response.data) {
                setCart(response.data);
                const total = Object.values(response.data).reduce((acc, curr) => acc + parseFloat(curr), 0);
                setTotalPrice(total);
            }
        } catch (error) {
            console.error("Error fetching cart data:", error);
        }
    };

    const addToCart = async () => {
        try {
            await axios.post('http://localhost:3001/addToCart', { seats: selectedSeats });
            fetchCartData();  // Refetch cart data after adding
            onSeatsSelected([]); // Clear the selected seats after adding to cart
        } catch (error) {
            console.error("Error adding to cart:", error);
        }
    };

    const removeFromCart = (seat) => {
        axios.post('http://localhost:3001/removeFromCart', { seat })
            .then(fetchCartData) // refresh the cart after removing a seat
            .catch(error => {
                console.error("Error removing from cart:", error);
            });
    };

    const emptyCart = () => {
        axios.post('http://localhost:3001/emptyCart')
            .then(response => {
                console.log(response.data);
                fetchCartData(); // refresh the cart after emptying it
            })
            .catch(error => {
                console.error("Error emptying the cart:", error);
            });
    };


    return (
        <div className="cart-container" style={{ width: '100%', }}>
            <h3 style={titleStyle}>Your Cart </h3><p>(Limited to 4 seats)</p>
              {/* Added styled header */}

            <ul>
                {
                    Object.entries(cart).map(([seat, price]) => {
                        const seatNumber = parseInt(seat.replace('seat', ''));
                        const formattedSeatNumber = seatNumber < 10 ? `0${seatNumber}` : seatNumber;
                        return (
                            <div key={seat} className="cart-item">
                                <span>Quantity 1 - Seat : {formattedSeatNumber} - Available for 1 movie - Price: {price} $ </span>
                                <button className="cart-button" onClick={() => removeFromCart(seat)}>Remove</button>
                            </div>
                        );
                    })
                }
            </ul>
            <div>
                Total Price: ${totalPrice.toFixed(2)}
            </div>
            <button className="" onClick={addToCart}>Add to Cart</button>
            <button className="" onClick={emptyCart}>Empty Cart</button>
            {showCode && (
    <div className="code-container" style={{ width: '100%', marginBottom: '20px', marginTop: '20px' }}>
        <pre style={{ padding: '20px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', whiteSpace: 'pre-wrap', overflowWrap: 'anywhere', background: '#f7f7f7' }}>
            <code>
{`/////////////////////////////////////////////// Seating Map
// Get the bitmap representation
app.get('/seating', (req, res) => {
    client.get('seats', (err, reply) => {
        if (err) return res.status(500).json({ error: 'Failed to fetch seats' });
        const bitmap = reply ? parseInt(reply, 2) : 0; // Convert binary string to integer
        res.json({ bitmap });
    });
});

// Set a seat or deselect
app.post('/setSeat', (req, res) => {
    const seatIndex = req.body.seatIndex;
    client.get('seats', (err, reply) => {
        if (err) return res.status(500).json({ error: 'Failed to fetch seats' });
        let bitmap = reply ? parseInt(reply, 2) : 0;

        // Toggle the bit
        bitmap ^= (1 << seatIndex);

        // Check if the number of set bits exceeds 8
        if (bitmap.toString(2).split('1').length - 1 > 8) {
            return res.status(400).json({ error: 'Booking is limited to 8 seats maximum.' });
        }

        client.set('seats', bitmap.toString(2).padStart(16, '0'), (error) => {
            if (error) return res.status(500).json({ error: 'Failed to update seat' });
            res.json({ bitmap });
        });
    });
});

app.post('/emptySelection', (req, res) => {
    client.set('seats', '0000000000000000', (err) => {
        if (err) {
            res.status(500).json({ error: "Failed to reset the seats" });
        } else {
            res.json({ message: "Seats reset successfully" });
        }
    });
});


//////////////////////////////////////////////;`}
            </code>
        </pre>
    </div>
)}


        </div>
    );
}



export default Cart;
