import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
    const [selectedSeats, setSelectedSeats] = useState([]);

    useEffect(() => {
        // Fetch the current selected seats when component mounts
        axios.get('/selectedSeats').then(response => {
            setSelectedSeats(response.data);
        });
    }, []);

    const addToCart = (seats) => {
        axios.post('/addToCart', { seats }).then(response => {
            alert(response.data);
            // Refresh the selected seats from server
            axios.get('/selectedSeats').then(response => {
                setSelectedSeats(response.data);
            });
        });
    };

    const removeFromCart = (seat) => {
        axios.post('/removeFromCart', { seat }).then(response => {
            alert(response.data);
            // Refresh the selected seats
            setSelectedSeats(seats => seats.filter(s => s !== seat));
        });
    };

    const clearCart = () => {
        axios.post('/clearCart').then(response => {
            alert(response.data);
            setSelectedSeats([]);
        });
    };

    return (
        <div>
            <h2>Your Cart</h2>
            {selectedSeats.map(seat => (
                <div key={seat}>
                    {seat} 
                    <button onClick={() => removeFromCart(seat)}>Remove</button>
                </div>
            ))}
            <button onClick={clearCart}>Clear Cart</button>
        </div>
    );
}

export default Cart;
