import React from 'react';

const cartPricePerSeat = 5; // We can also fetch this from the server if it varies.

function Cart({ cart }) {
    const totalSeats = Object.keys(cart).length;
    const totalPrice = totalSeats * cartPricePerSeat;

    return (
        <div>
            <h2>Your Cart</h2>
            <ul>
                {Object.keys(cart).map((seatKey) => (
                    <li key={seatKey}>{seatKey.replace('seat_', 'Seat ')}</li>
                ))}
            </ul>
            <h3>Total: ${totalPrice.toFixed(2)}</h3>
        </div>
    );
}

export default Cart;
