import React, { useState, useEffect } from 'react';

function GeoSpatialMap() {
    const [map, setMap] = useState(null);
    const [coords, setCoords] = useState({ lat: null, lng: null });

    // Initialize Google Map
    useEffect(() => {
        const initMap = () => {
            const initialCoords = { lat: -34.397, lng: 150.644 };
            const createdMap = new google.maps.Map(document.getElementById("map"), {
                zoom: 8,
                center: initialCoords,
            });

            createdMap.addListener('click', (e) => {
                setCoords(e.latLng.toJSON());
                new google.maps.Marker({
                    position: e.latLng,
                    map: createdMap,
                });
            });

            setMap(createdMap);
        };

        if (!window.google) {
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY_HERE&callback=initMap`;
            script.async = true;
            window.initMap = initMap;
            document.head.appendChild(script);
        } else {
            initMap();
        }
    }, []);

    const handleValidateCoordinates = async () => {
        try {
            const response = await fetch('/api/storeCoordinates', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(coords),
            });

            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error("Error storing coordinates:", error);
        }
    };

    return (
        <div>
            <div id="map" style={{ width: '100%', height: '500px' }}></div>
            <button onClick={handleValidateCoordinates}>
                Validate Coordinates
            </button>
        </div>
    );
}

export default GeoSpatialMap;
