import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Users() {
    const [users, setUsers] = useState([]);
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [premium, setPremium] = useState('No');  // New state for Premium
    const [geo, setGeo] = useState('Africa');  // Default to 'Africa', but you can set any default you want

    useEffect(() => {
        fetchUsers();
    }, []);

    // New state for showing or hiding the code
const [showCode, setShowCode] = useState(false);

// Function to toggle the visibility of the code block
const toggleCode = () => {
    setShowCode(prevState => !prevState);
}

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/users');
            setUsers(response.data);
        } catch (error) {
            console.error("Error fetching users", error);
        }
    };

    const addUserHandler = async () => {
        try {
            await axios.post('http://localhost:3001/users', { username, firstName, lastName, dateOfBirth, email });
            fetchUsers();
            setUsername('');
            setFirstName('');
            setLastName('');
            setPremium('No');
            setGeo('Africa');
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    const deleteUserHandler = async (userId) => {
        try {
            await axios.delete(`http://localhost:3001/users/${userId}`);
            fetchUsers();
        } catch (error) {
            console.error("Error deleting user", error);
        }
    };

    // CSS Styles
    const inputStyle = {
        padding: '10px',
        borderRadius: '5px',
        margin: '0 10px 20px 0',
        fontSize: '18px',
        width: 'calc(30% - 10px)'
    };

    const buttonStyleCode = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3a3a3a',
        color: 'white',
        border: 'none',
        cursor: 'pointer',
        marginRight: '10px'
    };

    const buttonStyle = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3498db',
        color: 'white',
        border: 'none',
        cursor: 'pointer',
        marginRight: '10px'
    };

    const titleStyle = {
        borderBottom: '2px solid #3498db',
        paddingBottom: '10px',
        marginTop: '20px'
    };
        // Updated CSS Styles
        const tableStyle = {
            width: '100%',
            marginTop: '20px',
            borderCollapse: 'collapse',
        };

        const tableHeaderStyle = {
            backgroundColor: '#3498db',
            color: 'white',
            fontWeight: 'bold',
            padding: '10px 15px',
            borderBottom: '2px solid #2980b9',
        };

        const tableRowStyle = {
            borderBottom: '1px solid #ccc',
        };

        const tableDataStyle = {
            padding: '10px 15px',
            textAlign: 'left',
        };

    return (
        <div>
            <h3 style={titleStyle}>Add User</h3>
            <div>
                <input style={inputStyle} value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
                <input style={inputStyle} value={firstName} onChange={(e) => setFirstName(e.target.value)} placeholder="First Name" />
                <input style={inputStyle} value={lastName} onChange={(e) => setLastName(e.target.value)} placeholder="Last Name" />
                <select style={inputStyle} value={premium} onChange={(e) => setPremium(e.target.value)}>
                    <option value="Yes">Yes</option>
                    <option value="No">No</option>
                </select>
                <select style={inputStyle} value={geo} onChange={(e) => setGeo(e.target.value)}>
                    <option value="Africa">Africa</option>
                    <option value="Asia">Asia</option>
                    <option value="Europe">Europe</option>
                    <option value="North America">North America</option>
                    <option value="South America">South America</option>
                    <option value="Australia">Australia</option>
                    <option value="Antarctica">Antarctica</option>
                </select>
                <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
            </div>

            <h3 style={titleStyle}>Registered Users</h3>
            <table className="ranking-table" style={{ width: '100%'}} >
                <thead>
                    <tr>
                        <th style={{ fontSize: '20px', textAlign: 'left'}}>Username</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>First Name</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Last Name</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Date of Birth</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Email</th>
                        <th style={{fontSize: '20px', textAlign: 'left'}}>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.userId} style={tableRowStyle}>
                            <td style={tableDataStyle}>{user.username}</td>
                            <td style={tableDataStyle}>{user.firstName}</td>
                            <td style={tableDataStyle}>{user.lastName}</td>
                            <td style={tableDataStyle}>{user.dateOfBirth}</td>
                            <td style={tableDataStyle}>{user.email}</td>
                            <td style={tableDataStyle}>
                                <button style={buttonStyle} onClick={() => deleteUserHandler(user.userId)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

           {/* Show/Hide Code button */}
  <div style={{ width: '100%', display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
          <button className="code-button" onClick={toggleCode} style={buttonStyleCode}>
              {showCode ? "Hide the Code" : "Show the Code"}
          </button>
      </div>

      {showCode && (
    <div className="code-container" style={{ width: '100%', marginBottom: '20px', marginTop: '20px' }}>
        <pre style={{ padding: '20px', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', whiteSpace: 'pre-wrap', overflowWrap: 'anywhere', background: '#f7f7f7' }}>
            <code>
{`/////////////////////////////////////////////// Users

// Endpoint to create a user
app.post('/users', (req, res) => {
    const { username, firstName, lastName, email, dob } = req.body;
    const userId = uuidv4();

    client.json_set(userId, '.', JSON.stringify({ username, firstName, lastName, email, dob }), (err) => {
        if (err) {
            console.error("Error storing user data in RedisJSON: ", err);
            return res.status(500).send(err.message);
        }

        client.sadd('users', userId, (err) => {
            if (err) {
                console.error("Error adding userId to Redis set: ", err);
                return res.status(500).send(err.message);
            }

            res.status(200).send({ username, firstName, lastName, email, dob, userId });
        });
    });
});



app.get('/users', (req, res) => {
    client.smembers('users', (err, userIds) => {
        if (err) {
            console.error("Error fetching userIds from Redis set: ", err);
            return res.status(500).send(err.message);
        }

        if (userIds.length === 0) {
            return res.status(200).send([]);
        }

        const users = [];
        let pending = userIds.length;

        for (let userId of userIds) {
            client.json_get(userId, '.', (err, userJson) => {
                if (err) {
                    console.error("Error fetching user data from RedisJSON for userId:", userId, err);
                    return res.status(500).send(err.message);
                }

                const user = JSON.parse(userJson);

                if (user) {  // Check if user isn't null or undefined
                    user.userId = userId;
                    users.push(user);
                } else {
                }

                pending--;
                if (pending === 0) {
                    res.status(200).send(users);
                }
            });
        }
    });
});


app.delete('/users/:userId', async (req, res) => {
    const userId = req.params.userId;

    try {
        // Delete the user JSON object from Redis
        await client.del(user:userId});

        // Remove that user ID from the SET
        await client.srem('users', userId);

        res.status(200).send({ message: 'User deleted successfully' });
    } catch (error) {
        console.error('Error deleting user:', error);
        res.status(500).send({ error: 'Failed to delete user' });
