import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Users() {
    const [userData, setUserData] = useState([]);
    const [userIDToDelete, setUserIDToDelete] = useState('');
    const [newUser, setNewUser] = useState({
        name: '',
        username: '',
        dob: '',
        phone: '',
        email: '',
    });

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/getUsers');
            setUserData(response.data);
        } catch (error) {
            console.error("Error fetching users", error);
        }
    };

    const deleteUserHandler = async (id) => {
        try {
            await axios.delete('http://localhost:3001/deleteUser', { data: { userID: id } });
            fetchUsers();
        } catch (error) {
            console.error("Error deleting user", error);
        }
    };

    const addUserHandler = async () => {
        try {
            await axios.post('http://localhost:3001/addUser', newUser);
            setNewUser({
                name: '',
                username: '',
                dob: '',
                phone: '',
                email: '',
            });
            fetchUsers();
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    // New styles (from the other component)
    const titleStyle = {
        borderBottom: '2px solid #3498db',
        paddingBottom: '10px',
        marginTop: '20px'
    };

    const buttonStyle = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3498db',
        color: 'white',
        border: 'none',
        cursor: 'pointer'
    };

    return (
        <div>
            <h3>Add New User</h3>
            <input 
                type="text" 
                placeholder="Name" 
                value={newUser.name} 
                onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            />
            {/* ... Same for other input fields ... */}
            <button onClick={addUserHandler}>Add User</button>

            <h3 style={titleStyle}>Existing Users</h3>
            <table className="users-table" style={{ width: '100%' }}>
                <thead>
                    <tr>
                        <th style={{ fontSize: '20px' }}>Name</th>
                        <th style={{ fontSize: '20px' }}>Username</th>
                        <th style={{ fontSize: '20px' }}>Date of Birth</th>
                        <th style={{ fontSize: '20px' }}>Phone</th>
                        <th style={{ fontSize: '20px' }}>Email</th>
                        <th style={{ fontSize: '20px' }}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {userData.map((user) => (
                        <tr key={user.id}>
                            <td style={{ fontSize: '20px' }}>{user.name}</td>
                            <td style={{ fontSize: '20px' }}>{user.username}</td>
                            <td style={{ fontSize: '20px' }}>{user.dob}</td>
                            <td style={{ fontSize: '20px' }}>{user.phone}</td>
                            <td style={{ fontSize: '20px' }}>{user.email}</td>
                            <td>
                                <button style={{ ...buttonStyle, backgroundColor: 'red' }} onClick={() => deleteUserHandler(user.id)}>
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Users;
