import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CounterSS() {
    const [element, setElement] = useState('');
    const [score, setScore] = useState('');
    const [selectedElement, setSelectedElement] = useState(null);
    const [elements, setElements] = useState([]);

    useEffect(() => {
        fetchElements();
    }, []);

    const fetchElements = async () => {
        try {
            const response = await axios.get('http://localhost:5000/getAllElements');
            setElements(response.data);
        } catch (error) {
            console.error("Error fetching elements", error);
        }
    };

    const addElementHandler = async () => {
        try {
            await axios.post('http://localhost:5000/addElement', { element, score });
            fetchElements();
        } catch (error) {
            console.error("Error adding element", error);
        }
    };

    const updateScoreHandler = async () => {
        try {
            await axios.put('http://localhost:5000/updateScore', { element: selectedElement, newScore: score });
            fetchElements();
        } catch (error) {
            console.error("Error updating score", error);
        }
    };

    return (
        <div>
            <input value={element} onChange={(e) => setElement(e.target.value)} placeholder="Enter element" />
            <input value={score} onChange={(e) => setScore(e.target.value)} placeholder="Enter score" />
            <button onClick={addElementHandler}>Add Element</button>

            <select value={selectedElement} onChange={(e) => setSelectedElement(e.target.value)}>
                <option value={null}>Select an element</option>
                {elements.map(el => <option key={el.element} value={el.element}>{el.element}</option>)}
            </select>
            <button onClick={updateScoreHandler}>Update Score</button>

            <table>
                <thead>
                    <tr>
                        <th>Element</th>
                        <th>Score</th>
                    </tr>
                </thead>
                <tbody>
                    {elements.map(el => (
                        <tr key={el.element}>
                            <td>{el.element}</td>
                            <td>{el.score}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default CounterSS;
