import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Users() {
    const [users, setUsers] = useState([]);
    const [username, setUsername] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [email, setEmail] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/users');
            setUsers(response.data);
        } catch (error) {
            console.error("Error fetching users", error);
        }
    };

    const addUserHandler = async () => {
        try {
            await axios.post('http://localhost:3001/users', { username, firstName, lastName, dateOfBirth, email });
            fetchUsers();
            setUsername('');
            setFirstName('');
            setLastName('');
            setDateOfBirth('');
            setEmail('');
        } catch (error) {
            console.error("Error adding user", error);
        }
    };

    // CSS Styles
    const inputStyle = {
        padding: '10px',
        borderRadius: '5px',
        margin: '0 10px 20px 0',
        fontSize: '18px',
        width: 'calc(30% - 10px)'
    };

    const buttonStyle = {
        padding: '10px 15px',
        borderRadius: '5px',
        backgroundColor: '#3498db',
        color: 'white',
        border: 'none',
        cursor: 'pointer'
    };

    const titleStyle = {
        borderBottom: '2px solid #3498db',
        paddingBottom: '10px',
        marginTop: '20px'
    };

    const userListStyle = {
        listStyleType: 'none',
        paddingLeft: '0',
        fontSize: '18px'
    };

    const userItemStyle = {
        borderBottom: '1px solid #ccc',
        padding: '10px 0'
    };

    return (
        <div>
            <h3 style={titleStyle}>Add User</h3>
            <div>
                <input style={inputStyle} value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
                <input style={inputStyle} value={firstName} onChange={(e) => setFirstName(e.target.value)} placeholder="First Name" />
                <input style={inputStyle} value={lastName} onChange={(e) => setLastName(e.target.value)} placeholder="Last Name" />
                <input style={inputStyle} type="date" value={dateOfBirth} onChange={(e) => setDateOfBirth(e.target.value)} placeholder="Date of Birth" />
                <input style={inputStyle} value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
                <button style={buttonStyle} onClick={addUserHandler}>Add User</button>
            </div>

            <h3 style={titleStyle}>Existing Users</h3>
            <ul style={userListStyle}>
                {users.map(user => (
                    <li key={user.id} style={userItemStyle}>
                        {user.username} - {user.firstName} {user.lastName} - {user.dateOfBirth} - {user.email}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Users;
