import React, { useState } from 'react';
import axios from 'axios';

function ApiCaching() {
    const [weather, setWeather] = useState({});
    const [city, setCity] = useState("Moscow"); // default to the capital of Russia
    const [responseTime, setResponseTime] = useState(null);

    const cities = [
        "Moscow",       // Russia
        "Ottawa",       // Canada
        "Beijing",      // China
        "New Delhi",    // India
        "Canberra",     // Australia
        "Brasília",     // Brazil
        "Astana",       // Kazakhstan
        "Buenos Aires", // Argentina
        "Algiers",      // Algeria
        "Riyadh"        // Saudi Arabia
    ];

    const fetchWeather = async (fromSource = false) => {
        const startTime = new Date().getTime();

        try {
            const response = await axios.get(`http://localhost:3001/weather?city=${city}${fromSource ? '&source=true' : ''}`);

            const endTime = new Date().getTime();
            setResponseTime(endTime - startTime);

            setWeather(response.data);
        } catch (error) {
            console.error('Failed to fetch weather:', error);
        }
    };

    return (
        <div>
            <h2>Weather for {weather.name}</h2>
            <div>Temperature: {weather.main && weather.main.temp} K</div>
            <div>Description: {weather.weather && weather.weather[0] && weather.weather[0].description}</div>
            <div>Response Time: {responseTime} ms</div>
            <div>
                <select value={city} onChange={e => setCity(e.target.value)}>
                    {cities.map(cityName => (
                        <option key={cityName} value={cityName}>
                            {cityName}
                        </option>
                    ))}
                </select>
                <button onClick={() => fetchWeather(false)}>Get Data from Cache</button>
                <button onClick={() => fetchWeather(true)}>Get Data from Source API</button>
            </div>
        </div>
    );
}

export default ApiCaching;
