import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        axios.get('/getCart').then(response => {
            setCartItems(Object.values(response.data.cart || {}));
        });
    }, []);

    const addToCart = () => {
        axios.post('/addToCart').then(response => {
            if (response.data.success) {
                axios.get('/getCart').then(response => {
                    setCartItems(Object.values(response.data.cart || {}));
                });
            }
        });
    };

    const emptyCart = () => {
        axios.post('/emptyCart').then(response => {
            if (response.data.success) {
                setCartItems([]);
            }
        });
    };

    const removeFromCart = (seatIndex) => {
        axios.post('/removeFromCart', { seatIndex }).then(response => {
            if (response.data.success) {
                const updatedCartItems = cartItems.filter(item => item !== seatIndex.toString());
                setCartItems(updatedCartItems);
            }
        });
    };

    return (
        <div>
            <button onClick={addToCart}>Add to Cart</button>
            <button onClick={emptyCart}>Empty Cart</button>
            <ul>
                {cartItems.map((seat, index) => (
                    <li key={index}>
                        Seat: {seat}
                        <button onClick={() => removeFromCart(seat)}>Remove</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Cart;
