import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CounterSS() {
    const [rankingData, setRankingData] = useState([]);
    const [element, setElement] = useState('');
    const [score, setScore] = useState('');
    const [selectedElement, setSelectedElement] = useState('');
    const [selectedElementScore, setSelectedElementScore] = useState('');
    const [elements, setElements] = useState([]);

    useEffect(() => {
        fetchElements();
        fetchRanking();  // Add this line
    }, []);


    const fetchElements = async () => {
        try {
            const response = await axios.get('http://localhost:3001/getAllElements');
            setElements(response.data);
        } catch (error) {
            console.error("Error fetching elements", error);
        }
    };

    const fetchRanking = async () => {
        try {
            const response = await axios.get('http://localhost:3001/getRanking');
            setRankingData(response.data);
        } catch (error) {
            console.error("Error fetching ranking", error);
        }
    };


    const addElementHandler = async () => {
        try {
            await axios.post('http://localhost:3001/addElement', { element, score });
            fetchElements();
            setElement('');
            setScore('');
        } catch (error) {
            console.error("Error adding element", error);
        }
    };

    const updateScoreHandler = async () => {
        try {
            await axios.put('http://localhost:3001/updateScore', { element: selectedElement, newScore: selectedElementScore });
            fetchElements();
            setSelectedElement('');
            setSelectedElementScore('');
        } catch (error) {
            console.error("Error updating score", error);
        }
    };

    return (
        <div>
            <h2>Add Element to Sorted Set</h2>
            <div>
                <input value={element} onChange={(e) => setElement(e.target.value)} placeholder="Enter element" />
                <input type="number" value={score} onChange={(e) => setScore(e.target.value)} placeholder="Enter score" />
                <button onClick={addElementHandler}>Add Element</button>
            </div>

            <h2>Update Score of an Element</h2>
            <div>
                <select value={selectedElement} onChange={(e) => {
                    setSelectedElement(e.target.value);
                    const matchedElement = elements.find(el => el.element === e.target.value);
                    if (matchedElement) {
                        setSelectedElementScore(matchedElement.score);
                    }
                }}>
                    <option value="">Select an element</option>
                    {elements.map(el => <option key={el.element} value={el.element}>{el.element}</option>)}
                </select>
                <input type="number" value={selectedElementScore} onChange={(e) => setSelectedElementScore(e.target.value)} placeholder="Enter new score" />
                <button onClick={updateScoreHandler}>Update Score</button>
            </div>

            <h2>Elements Ranked by Score</h2>
            <table className="ranking-table">
    <thead>
        <tr>
            <th>Rank</th>
            <th>Element</th>
            <th>Score</th>
        </tr>
    </thead>
    <tbody>
        {rankingData.map((data, index) => (
            <tr key={index}>
                <td>{index + 1}</td>
                <td>{data.element}</td>
                <td>{data.score}</td>
            </tr>
        ))}
    </tbody>
</table>
        </div>
    );
}

export default CounterSS;
