import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = 5; // $5 per seat

function Cart() {
    const [cart, setCart] = useState({});
    const [selectedSeats, setSelectedSeats] = useState([]);
    const [newlySelectedSeats, setNewlySelectedSeats] = useState([]);


    useEffect(() => {
        fetchCart();
        fetchSelectedSeats();
    }, []);

    const fetchSelectedSeats = () => {
        axios.get('http://localhost:3001/seating')
            .then(response => {
                const bitmap = response.data.bitmap;
                const selected = Array.from(bitmap.toString(2).matchAll(/1/g), m => 'A' + (m.index + 1));
                setSelectedSeats(selected);
            })
            .catch(() => alert("Error fetching the selected seats."));
    };

    const fetchCart = () => {
        axios.get('http://localhost:3001/cart')
            .then(response => {
                setCart(response.data.cart);
            })
            .catch(() => alert("Error fetching the cart."));
    };

    const handleSeatSelection = (seat) => {
        let updatedSeats;

        // If the seat is already in the array, remove it (deselection).
        if (newlySelectedSeats.includes(seat)) {
            updatedSeats = newlySelectedSeats.filter(s => s !== seat);
        } else {
            // Otherwise, add the seat to the array (selection).
            updatedSeats = [...newlySelectedSeats, seat];
        }

        setNewlySelectedSeats(updatedSeats);
    };


    const addToCart = () => {
        axios.post('http://localhost:3001/addToCart', { selectedSeats: newlySelectedSeats })
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                    fetchSelectedSeats();
                    setNewlySelectedSeats([]); // Reset the selected seats after adding to cart
                } else {
                    alert(response.data.error);
                }
            })
            .catch(error => {
                console.error('Error while communicating with the server:', error.response ? error.response.data : error.message);
                alert("Error adding to the cart.");
            });
                };


    const clearCart = () => {
        axios.post('http://localhost:3001/clearCart')
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                }
            })
            .catch(() => alert("Error clearing the cart."));
    };

    const removeSeat = (seatIndex) => {
        axios.post(`http://localhost:3001/removeFromCart`, { seat: seatIndex })
            .then(response => {
                if (response.data.success) {
                    fetchCart();
                }
            })
            .catch(() => alert("Error removing the seat from the cart."));
    };

    return (
        <div>
            {/* Add to Cart Button */}
            <div>
                <button onClick={addToCart}>Add to Cart</button>
            </div>

            {/* Cart Representation */}
            <div>
                {Object.entries(cart).map(([seatPosition, count]) => (
                    <div key={seatPosition}>
                        <span>Seat {seatPosition}: </span>
                        <span>{count} x ${SEAT_PRICE} = ${count * SEAT_PRICE}</span>
                        <button onClick={() => removeSeat(seatPosition)}>Remove</button>
                    </div>
                ))}
            </div>

            {/* Cart Total */}
            <div>
                Total: ${Object.values(cart).reduce((acc, count) => acc + count * SEAT_PRICE, 0)}
            </div>

            {/* Clear Cart Button */}
            <div>
                <button onClick={clearCart}>Clear Cart</button>
            </div>
        </div>
    );
}

export default Cart;
