import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = 5;  // Assuming each seat costs $5

function Cart() {
    const [cart, setCart] = useState({});
    const [total, setTotal] = useState(0);

    useEffect(() => {
        // Fetch the cart content on component mount
        axios.get('http://localhost:3001/cart')
        .then(response => {
            setCart(response.data.cart);
            setTotal(response.data.total);
        })
        .catch(() => {
            alert("Error fetching the cart");
        });
    }, []);

    return (
        <div>
            <button onClick={() => {
                axios.post('http://localhost:3001/addToCart')
                .then(response => {
                    setCart(response.data.cart);
                    setTotal(response.data.total);
                })
                .catch(() => {
                    alert("Error adding to the cart.");
                });
            }}>
                Add to Cart
            </button>

            <div>
                <h3>Shopping Cart</h3>
                {Object.entries(cart).map(([seat, _], index) => (
                    <p key={index}>Seat {seat} - ${SEAT_PRICE}</p>
                ))}
                <strong>Total: ${total}</strong>
            </div>
        </div>
    );
}

export default Cart;
