import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  // States for input fields
  const [username, setUsername] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [dob, setDob] = useState('');


  const containerStyle = {
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  };

  const formGroupStyle = {
    marginBottom: '10px'
  };

  const buttonStyle = {
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '4px',
    cursor: 'pointer',
    marginTop: '10px'
  };

  const userListStyle = {
    listStyleType: 'none',
    padding: '0'
  };

  const userListItemStyle = {
    cursor: 'pointer',
    padding: '5px',
    transition: 'background-color 0.2s'
  };

  useEffect(() => {
    async function fetchUsers() {
      const response = await axios.get('http://localhost:3001/users');
      setUsers(response.data);
    }

    fetchUsers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = {
      username,
      firstName,
      lastName,
      email,
      dob
    };

    const response = await axios.post('http://localhost:3001/users', newUser);
    if (response.status === 200) {
      setUsers([...users, response.data]);
      setUsername('');
      setFirstName('');
      setLastName('');
      setEmail('');
      setDob('');
    }
  };

  const handleUserClick = async (userId) => {
    const response = await axios.get(`http://localhost:3001/users/${userId}`);
    setSelectedUser(response.data);
  };

  return (
    <div style={containerStyle}>
      <h2>Create New User</h2>
      <form onSubmit={handleSubmit}>
        <div style={formGroupStyle}>
          {/* ... rest of your input fields with similar structure */}
        </div>
        <button style={buttonStyle} type="submit">Add User</button>
      </form>

      <h2>Users List</h2>
      <ul style={userListStyle}>
        {users.map((user) => (
          <li key={user.userId} style={userListItemStyle} onClick={() => handleUserClick(user.userId)}>
            {user.username} - {user.firstName} {user.lastName}
          </li>
        ))}
      </ul>

      {selectedUser && (
        <div>
          <h3>Selected User Details</h3>
          <p>Username: {selectedUser.username}</p>
          <p>First Name: {selectedUser.firstName}</p>
          <p>Last Name: {selectedUser.lastName}</p>
          <p>Email: {selectedUser.email}</p>
          <p>Date of Birth: {selectedUser.dob}</p>
        </div>
      )}
    </div>
  );
};

export default Users;
