import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SEAT_PRICE = //define your seat price here, say 10;

function Cart() {
    const [cartItems, setCartItems] = useState({});
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        // Fetch the selected seats and add them to the cart
        axios.post('/AddToCart')
            .then(response => {
                fetchCartItems();
            })
            .catch(error => {
                console.error("Error adding to cart:", error.response?.data || error.message);
            });
    }, []);

    const fetchCartItems = () => {
        // Fetch the cart hash from Redis (assuming you have an endpoint for this, if not you'll need to create one)
        axios.get('/fetchCartItems')
            .then(response => {
                setCartItems(response.data);
                const total = Object.values(response.data).reduce((acc, price) => acc + parseFloat(price), 0);
                setTotalPrice(total);
            })
            .catch(error => {
                console.error("Error fetching cart items:", error.response?.data || error.message);
            });
    }

    return (
        <div className="cart">
            <h2>Shopping Cart</h2>
            <ul>
                {Object.entries(cartItems).map(([seat, price]) => (
                    <li key={seat}>
                        Seat {seat}: ${price}
                    </li>
                ))}
            </ul>
            <div>Total: ${totalPrice.toFixed(2)}</div>
        </div>
    );
}

export default Cart;
