import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import axios from 'axios';

function GeoSpatialMap() {
    const [stations, setStations] = useState([]);
    const defaultPosition = [51.505, -0.09]; // You can set this to your preferred default

    useEffect(() => {
        fetchNearbyStations(defaultPosition);
    }, []);

    const fetchNearbyStations = async (position) => {
        try {
            const response = await axios.get('http://localhost:3001/api/search-nearby', {
                params: {
                    lat: position[0],
                    lon: position[1]
                }
            });
            if (response.data.stations) {
                setStations(response.data.stations);
            }
        } catch (error) {
            console.error('Error fetching nearby stations:', error);
        }
    };

    const LocationMarker = () => {
        const map = useMapEvents({
            click(event) {
                map.locate();
            },
            locationfound(e) {
                fetchNearbyStations([e.latlng.lat, e.latlng.lng]);
                map.flyTo(e.latlng, map.getZoom());
            },
        });
        return null;
    };

    return (
        <div style={{ height: "500px", width: "100%" }}>
            <MapContainer center={defaultPosition} zoom={13} style={{ height: "100%", width: "100%" }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                <LocationMarker />
                {stations.map((station) => (
                    <Marker key={station.id} position={[station.lat, station.lon]}>
                        <Popup>
                            {station.name}<br />
                            Distance: {station.distance} m
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>
        </div>
    );
}

export default GeoSpatialMap;
