import React, { useState } from 'react';
import axios from 'axios';

function Cart() {
    const [cart, setCart] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    const SEAT_PRICE = 5; // You can change this to the price you want

    const addToCart = () => {
        axios.get('http://localhost:3001/seating').then(response => {
            const bitmap = response.data.bitmap;
            const selectedSeats = Array.from({ length: 16 })
                .map((_, index) => (bitmap & (1 << index)) ? index : -1)
                .filter(index => index !== -1);

            setCart(selectedSeats);
            setTotalPrice(selectedSeats.length * SEAT_PRICE);
        });
    };

    return (
        <div>
            <button onClick={addToCart}>Add to Cart</button>
            <ul>
                {cart.map(seat => (
                    <li key={seat}>Seat {seat + 1}: ${SEAT_PRICE}</li>
                ))}
            </ul>
            <p>Total: ${totalPrice}</p>
        </div>
    );
}

export default Cart;
