import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ApiCaching() {
    const [weather, setWeather] = useState({});
    const [city, setCity] = useState("Seattle");
    const [showCode, setShowCode] = useState(false);

    const fetchWeather = async (forceRefresh = false) => {
        try {
            const response = await axios.get(`http://localhost:3001/weather?city=${city}`)
            ;
            setWeather(response.data);
        } catch (error) {
            console.error('Failed to fetch weather:', error);
        }
    };

    useEffect(() => {
        fetchWeather();
        const interval = setInterval(() => {
            fetchWeather();
        }, 10 * 60 * 1000); // Refresh every 10 minutes

        return () => clearInterval(interval);
    }, [city]);


    return (
        <div>
<h2>Weather for {weather.name}</h2>
<div>Temperature: {weather.main && weather.main.temp} K</div>  {/* Note that temperature is in Kelvin by default */}
<div>Description: {weather.weather && weather.weather[0] && weather.weather[0].description}</div>
{/* The API doesn't provide a 'timestamp' field in the response, so I'm omitting that for now */}

            <button onClick={() => fetchWeather(true)}>Refresh Weather</button>
            <input value={city} onChange={e => setCity(e.target.value)} />

       </div>
    );
}

export default ApiCaching;
